	 * api reasonable.  You can provide public methods for those you
	 * actually need in Asset.
	 */
	public function getAccessLogsJoinsfGuardUserProfile($criteria = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
		if ($criteria === null) {
			$criteria = new Criteria(AssetPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collAccessLogs === null) {
			if ($this->isNew()) {
				$this->collAccessLogs = array();
			} else {

				$criteria->add(AccessLogPeer::ASSET_ID, $this->id);

				$this->collAccessLogs = AccessLogPeer::doSelectJoinsfGuardUserProfile($criteria, $con, $join_behavior);
			}
		} else {
			// the following code is to determine if a new query is
			// called for.  If the criteria is the same as the last
			// one, just return the collection.

			$criteria->add(AccessLogPeer::ASSET_ID, $this->id);

			if (!isset($this->lastAccessLogCriteria) || !$this->lastAccessLogCriteria->equals($criteria)) {
				$this->collAccessLogs = AccessLogPeer::doSelectJoinsfGuardUserProfile($criteria, $con, $join_behavior);
			}
		}
		$this->lastAccessLogCriteria = $criteria;

		return $this->collAccessLogs;
	}

	/**
